let url="http://localhost:3000",primus=Primus.connect(url,{reconnect:{max:1/0,min:500,retries:10}});function appendBalance(){fetch("http://localhost:3000/api/v1/leaderboard/current",{method:"get",headers:{Authorization:"Bearer "+localStorage.getItem("token"),"Content-Type":"application/json"}}).then(e=>e.json()).then(e=>{console.log(e),document.querySelector(".currentBalanceNumber").innerHTML="$ "+e.data.user[0].balance}).catch(e=>{console.log(e)})}primus.on("data",e=>{console.log(e),"update"===e.action&&appendBalance()}),window.onload=appendBalance();