function appendBalance(){fetch("http://localhost:3000/api/v1/leaderboard/current",{method:"get",headers:{Authorization:"Bearer "+localStorage.getItem("token"),"Content-Type":"application/json"}}).then(e=>e.json()).then(e=>{console.log(e),document.querySelector(".currentBalanceNumber").innerHTML="$ "+e.data.user[0].balance}).catch(e=>{console.log(e)})}window.onload=appendBalance();