function appendInfo(){fetch("/api/v1/leaderboard/current",{method:"get",headers:{Authorization:"Bearer "+localStorage.getItem("token"),"Content-Type":"application/json"}}).then(e=>e.json()).then(e=>{console.log(e),document.querySelector(".fullName").innerHTML=e.data.user[0].firstName+" "+e.data.user[0].lastName}).catch(e=>{console.log(e)})}window.onload=appendInfo();