let url=new URL(document.URL),path=url.pathname,split=path.split("/"),id=split[2];fetch("/api/v1/transfers/"+id,{method:"get",headers:{Authorization:"Bearer "+localStorage.getItem("token"),"Content-Type":"application/json"}}).then(e=>e.json()).then(e=>{console.log(e),document.querySelector(".from").innerHTML=e.data.transfer[0].from,document.querySelector(".to").innerHTML=e.data.transfer[0].to,document.querySelector(".amount").innerHTML="$"+e.data.transfer[0].IMDollars,document.querySelector(".reason").innerHTML=e.data.transfer[0].reason,e.data.transfer[0].message?document.querySelector(".description").innerHTML=e.data.transfer[0].message:document.querySelector(".description").innerHTML="No description provided"}).catch(e=>{console.log(e)});